import React from "react";

class CreateTodo extends React.Component{
    constructor(props){
        super(props);

        this.onChangeTodoDescription = this.onChangeTodoDescription.bind(this);
        this.onChangeTodoResponsible = this.onChangeTodoResponsible.bind(this);
        this.onChangeTodoPriority = this.onChangeTodoPriority.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state ={
            todoTitle:'',
            todoDescription:'',
            todoResponsibility:'',
            todoPriority:'',
            todoCompleted:false,
            // future feature edit-history versioning
        }

    }
    onChangeTodoDescription(e){
        this.setState({
            todoDescription: e.target.value
        });
    }
    onChangeTodoResponsibility(e){
        this.setState({
            todoResponsibility: e.target.value
        });
    }
    onChangeTodoPriority(e){
        this.setState({
            todoPriority: e.target.value
    });
}
    onSubmit(e){
        e.preventDefault();

        console.log('Form Submitted');
        console.log('New Todo Description: ${this.state.todoDescription}');
        console.log('New Todo Responsibility: ${this.state.todoResponsibility}');
        console.log('New Todo Priority: ${this.state.todoPriority}');

        this.setState({
            todoDescription:'',
            todoResponsibility:'',
            todoPriority:'',
            todoCompleted:false
        })
    }
    render(){
        return(
            <div>
                <h2>This is the Create Todo Component</h2>
                <label>
                    Description
                    <textarea onChange= {this.onChangeTodoDescription} placeholder='describe your todo here '></textarea>
                </label>
                <label>
                    Responsibility
                    <input type='text' placeholder="Who's task is it?" onChance={this.onChangeTodoResponsibility}></input>
                </label>
                <label>
                    Priotity
                    <select onChange={this.onChangeTodoPriority}>
                        <option>Low</option>
                        <option>Medium</option>
                        <option>High</option>
                        <option>Top</option>
                    </select>
                </label>
                <input type= 'Submit' onChange={this.onSubmit}></input>

            </div>
        )
    }
}


export default CreateTodo;
