import React from "react";
import Todo from "../components/todo.component";
import axios from "axios";
import Modal from "../components/modal.components";
import CreateTodo from "./createTodo.component";

class TodoList extends React.Component {
  constructor(props) {
    super(props);
    this.state = { todos: [], modalData: {} };
  }
  componentDidMount() {
    axios
      .get("http://localhost:4000/todos/")
      .then(response => {
        this.setState({ todos: response.data });
      })
      .catch(function(error) {
        console.log(error);
      });
  }
  // CREATE -------------------------------------------------------------------
  createTodoClick =() =>{

  }

  // END CREATE ---------------------------------------------------------------

  // EDIT----------------------------------------------------------------------
  editOnClick = currentTodo => {
    console.log("EditOnClick triggered");

    // set state of modal
    this.setState({
      modalData: { contentToDisplay: <EditTodo todo={props.data} submitEdit={props.submitEdit}/>,
                   currentTodo: currentTodo }
                  });
  };

  editOnSubmit = todoId => {
    console.log("Beginning of editOnSubmit in todoList component");
    axios.get("http://localhost:4000/todos/" + todoId).then(res => {
      const updatedTodo = res.data;
      console.log("db response: ", res.data);
      const arrayCopy = this.state.todos.map(currentTodo => {
        console.log("updatedTodo: ", updatedTodo);
        if (updatedTodo._id === currentTodo._id) {
          return updatedTodo;
        }
        return currentTodo;
      });
      console.log("arrayCopy: ", arrayCopy);
      this.setState({
        todos: arrayCopy,
        modalData: {}
      });
    });

    console.log("END of todoList's editOnSubmit function");
  };
// END EDIT -------------------------------------------------------------------
  delete = todoId => {
    const arrayCopy = this.state.todos.filter(todo => todo._id !== todoId);

    axios.delete("http://localhost:4000/todos/" + todoId).catch(error => {
      console.log(error);
    });

    this.setState({
      todos: arrayCopy
    });
  };
// MODAL ----------------------------------------------------------------------
  modalClose = () => {
    // closes modal
    this.setState({
      modalData: {}
    });
  };
// END MODAL ------------------------------------------------------------------
  render() {
    const modalData = this.state.modalData;
    console.log("modalData: ", modalData);
    let modal;
    if (this.state.modalData.currentTodo) {
      modal = (
        <Modal
          handleClose={this.modalClose}
          data={modalData}
          submitEdit={this.editOnSubmit}
        />
      );
    // } else if (this.state.modalData.newTodo){
    //   modal = (
    //     <Modal 
    //     handleClose={this.modalClose}
    //     data={modalData}
    //     submitClick={this.createTodoSubmit}

    //     />
    //   );
    };
    const list = this.state.todos.map((currentTodo, i) => {
      return (
        <Todo
          delete={this.delete}
          editClick={this.editOnClick}
          todo={currentTodo}
          key={i}
        />
      );
    });

    return (
      <div className="container">
        <h2>Todo List Header</h2>
        <div className="contain-table">
          <table className="striped-table">
            <thead>
              <tr>
                <th>Description</th>
                <th>Responsibility</th>
                <th>Priority</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>{list}</tbody>
          </table>
        </div>
        {<CreateTodo />}
        {modal}
      </div>
    );
  }
}

export default TodoList;
